<?phpfunction folder_id_validation(){    return isset($_GET['folder'])        && is_numeric($_GET['folder'])        && $_GET['folder'] > 0;}function add_folder_validation(){     return isset($_POST['folder_title'])        && is_string($_POST['folder_title'])        && strlen($_POST['folder_title']) > 3;}function add_task_validation(){    return isset($_POST['task_title'])        && is_string($_POST['task_title'])        && strlen($_POST['task_title']) > 3;}function delete_folder_validation(){    return isset($_GET['delete_folder'])        && is_numeric($_GET['delete_folder']);}function delete_task_validation(){    return isset($_GET['delete_task'])        && is_numeric($_GET['delete_task']);}function register_validation(){    return isset($_POST['username'])        && isset($_POST['email'])        && isset($_POST['password'])        && strlen($_POST['username']) > 3        && strlen($_POST['email']) > 3        && count_chars($_POST['password']) > 8        && filter_var($_POST['email'],FILTER_VALIDATE_EMAIL)        ;}function login_validation(){    return isset($_POST['email'])        && isset($_POST['password'])        && strlen($_POST['email']) > 3        && count_chars($_POST['password']) > 8        && filter_var($_POST['email'],FILTER_VALIDATE_EMAIL)        ;}