<?phpsession_start();include "library/helpers.php";include 'bootstrap/config.php';include 'bootstrap/constans.php';include 'vendor/autoload.php';try {    $DB = new PDO("mysql:host=$database_config->host;dbname=$database_config->db",$database_config->username,$database_config->password);}catch (PDOException $e){    dd($e->getMessage());}function doQuery($sql ,$values=[]){    global $DB;    $stmt = $DB->prepare($sql);    $stmt->execute($values);    return $stmt->rowCount();}function doSelect($sql ,$values=[] , $fetch_type = PDO::FETCH_OBJ ){    global $DB;    $stmt = $DB->prepare($sql);    $stmt->execute($values);    return $stmt->fetchAll($fetch_type);}include "library/auth_lib.php";include "library/folders_lib.php";include "library/tasks_lib.php";include "app/validation.php";