<?phpinclude 'bootstrap/init.php';if (!auth_check()){    redirect_to(route('auth.php'));}if (isset($_GET['logout'])){    logout();    redirect_to(route('auth.php'));}if (delete_folder_validation()){    delete_folder($_GET['delete_folder']);}if (delete_task_validation()){    delete_task($_GET['delete_task']);    redirect_to(BASE_URL,'folder',$_GET['folder']);}if (add_folder_validation()){    switch ($_POST['action'])    {        case 'add_folder' : add_folder($_POST['folder_title']);        break;    }}if (add_task_validation()){    switch ($_POST['action'])    {        case 'add_task' : add_task($_POST['task_title'] ,$_POST['folder_id'] );          redirect_to(BASE_URL,'folder',$_POST['folder_id']);            break;    }}$folders = get_folders();if (!folder_id_validation()) $_GET['folder'] = null ;$tasks = get_tasks($_GET['folder']);include "view/index.php";