<?phpfunction get_folders(){    global $DB;    $sql = "select * from folders where user_id=:user_id";    $stmt = $DB->prepare($sql);    $stmt->execute(['user_id'=>auth_id()]);    return $stmt->fetchAll(PDO::FETCH_OBJ);}function delete_folder($id){    global $DB;    $sql = 'delete from folders where id = :id';    $stmt = $DB->prepare($sql);    $stmt->execute(['id'=>$id]);    return $stmt->rowCount();}function add_folder($folder_name){    global $DB;    $sql = 'insert into folders (title , user_id) values (:title , :user_id)';    $stmt = $DB->prepare($sql);    $stmt->execute(['title' => $folder_name , 'user_id' => auth_id()]);    return $stmt->rowCount();}