<?phpfunction dd($value){    if (is_object($value) | is_array($value)) write_arr($value);        else write($value);    die();}function write_arr($value){    echo "<pre style='padding:30px ; background-color: #f9dede ; color: #521717 ;width: 80%;margin: 50px auto;'>";    print_r($value);    echo "</pre>";}function write($value){    echo "<div style='padding:30px ; background-color: #f9dede ; color: #521717 ;width: 80%;margin: 50px auto;'>           $value          </div>";}function redirect_to($url , $param=null , $value=null){    if (is_null($param) && is_null($value))    {        header("location: $url");    }    header("location: $url?$param=$value");}function route($uri=''){    return BASE_URL . $uri;}