<?phpfunction delete_task($id){    $sql ='delete from tasks where id = :id';    $record = doQuery($sql , ['id' => $id]);    return $record;}function get_tasks($folder_id){    global $DB;    $sql = 'select * from tasks where folder_id = :folder_id';    $stmt = $DB->prepare($sql);    $stmt->execute(['folder_id' => $folder_id]);    return $stmt->fetchAll(PDO::FETCH_OBJ);}function add_task($task_title , $folder_id){    $sql = 'insert into tasks (title , folder_id , user_id) values (:title , :folder_id , :user_id)';    $records = doQuery($sql , ['title' => $task_title , 'folder_id' => $folder_id , 'user_id'=>auth_id()]);    return $records;}